// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

import "./TrustScore.sol";
import "./LoanManager.sol";
import "./PoolManager.sol";
import "./OfferManager.sol";
import "./Activity.sol";
import "./StakingManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "../math/SimpleMath.sol";
import "../libraries/AssetLibrary.sol";
import "../libraries/OfferLibrary.sol";
import "../data/PriceFeed.sol";
import "../data/TokenFeed.sol";

contract Borrowing is Context {
    using SafeMath for uint256;

    Activity private _activity;
    PoolManager private _poolManager;
    LoanManager private _loanManager;
    OfferManager private _offerManager;
    StakingManager private _stakingManager;
    TrustScore private _trustScore;
    TokenFeed private _tokenFeed;
    PriceFeed private _priceFeed;

    constructor(
        address poolManager_,
        address loanManager_,
        address offerManager_,
        address activity_,
        address stakingManager_,
        address trustScore_,
        address tokenFeed_,
        address priceFeed_
    ) {
        _activity = Activity(activity_);
        _poolManager = PoolManager(poolManager_);
        _loanManager = LoanManager(loanManager_);
        _offerManager = OfferManager(offerManager_);
        _stakingManager = StakingManager(stakingManager_);
        _trustScore = TrustScore(trustScore_);
        _priceFeed = PriceFeed(priceFeed_);
        _tokenFeed = TokenFeed(tokenFeed_);
    }

    function createBorrowingOffer(
        AssetLibrary.Type principalType,
        uint256 principal, // for ERC20 assets
        AssetLibrary.Type collateralType,
        uint256 _collateral, // for ERC20 assets
        uint256 interest,
        uint256 daysToMaturity,
        uint256 daysToExpire
    ) public payable {
        uint256 collateral;

        /* checks if the borrower is a defaulter */
        require(!_activity.isDefaulter(_msgSender()), "ERR_UNSAFE_BORROWER");

        /* derive the address of the collateral from data repository */
        address collateralToken = _tokenFeed.getTokenAddress(collateralType);

        /* extract collateral from borrower */
        if (collateralToken == _tokenFeed.nativeAddress()) {
            collateral = msg.value;
            (bool success, ) = _poolManager.getAddress().call{
                value: collateral
            }("");
            require(success, "ERR_TRANSFER_NATIVE");
        } else {
            collateral = _collateral;
            IERC20(collateralToken).transferFrom(
                _msgSender(),
                address(_poolManager),
                collateral
            );
        }

        /* deposit tokens to vault */
        require(
            _poolManager.deposit(
                _msgSender(),
                collateralToken,
                collateral
            ),
            "ERR_POOL_MANAGER_DEPOSIT"
        );

        /* allocate staking reward */
        require(
            _stakingManager.provideLiquidityFor(
                _msgSender(),
                collateralToken,
                collateral
            ),
            "ERR_STAKING_MANAGER_PROVIDE"
        );

        /* create the offer */
        require(
            _offerManager.createBorrowingOffer(
                principalType,
                principal,
                collateralType,
                collateral,
                interest,
                daysToMaturity,
                daysToExpire,
                _msgSender()
            ) > 0,
            "ERR_OFFER_MANAGER_CREATE"
        );
    }

    function acceptLendingOffer(
        uint256 offerId,
        uint256 desiredAmount,
        AssetLibrary.Type collateralType
    ) public payable {
        OfferLibrary.LendingOffer memory offer = _offerManager.getLendingOffer(
            offerId
        );

        require(
            desiredAmount <= offer.principal,
            "ERR_INSUFFICIENT_AMOUNT"
        );

        /* checks if the lender is a defaulter */
        /* checks if the borrower is a defaulter */
        require(!_activity.isDefaulter(offer.lender), "ERR_UNSAFE_LENDER");
        require(!_activity.isDefaulter(_msgSender()), "ERR_UNSAFE_BORROWER");

        /* derive the address of the asset types from data repository */
        address principalToken = _tokenFeed.getTokenAddress(offer.principalType);
        address collateralToken = _tokenFeed.getTokenAddress(collateralType);

        /* get the trust factor of the borrower */
        uint256 pendingLoans = _activity.pendingLoans(_msgSender());
        (, uint256 percentage) = _trustScore.trustGrade(
            _msgSender(),
            pendingLoans
        );

        /* calculate the collateral amount */
        uint256 fullCollateral = _priceFeed.estimate(
            principalToken,
            collateralToken,
            desiredAmount
        );

        uint256 collateral = SimpleMath.percentageOf(
            fullCollateral,
            percentage
        );

        /* extract collateral tokens from borrower */
        if (collateralToken == _tokenFeed.nativeAddress()) {
            require(collateral >= msg.value, "ERR_COLLATERAL_AMOUNT");
            // (bool success, ) = _poolManager.getAddress().call{
            //     value: collateral
            // }("");
            // require(success, "ERR_TRANSFER_NATIVE");
        } else {
            IERC20(collateralToken).transferFrom(
                _msgSender(),
                address(_poolManager),
                collateral
            );
        }

        require(
            _poolManager.deposit(
                _msgSender(),
                collateralToken,
                collateral
            ),
            "ERR_POOL_MANAGER_DEPOSIT"
        );

        /* allocate staking reward for lender on collateral */
        require(
            _stakingManager.provideLiquidityFor(
                offer.lender,
                collateralToken,
                collateral
            ),
            "ERR_STAKING_MANAGER_PROVIDE"
        );

        /* unallocate staking reward for lender on principal */
        require(
            _stakingManager.withdrawLiquidityFor(
                offer.lender,
                principalToken,
                desiredAmount
            ),
            "ERR_STAKING_MANAGER_PROVIDE"
        );

        /* transfer the asset to borrower */
        // _poolManager.transfer(
        //     _msgSender(),
        //     principalToken,
        //     desiredAmount,
        //     principalToken == _tokenFeed.nativeAddress()
        // );

        /* update offer balance */
        _offerManager.deductOfferAmount(offerId, desiredAmount);

        /* register the loan */
        _loanManager.createLoan(
            offerId,
            OfferLibrary.Type.LENDING_OFFER,
            offer.principalType,
            collateralType,
            desiredAmount,
            collateral,
            offer.interest,
            offer.daysToMaturity,
            _msgSender(),
            offer.lender
        );

        /* update experience */
        // amount in USD
        _activity.incrementBorrowed(_msgSender(), 0);
    }

    function repayLoan(uint256 loanId) public payable {
        LoanLibrary.Loan memory loan = _loanManager.getLoan(loanId);

        bool liquidated = loan.status == LoanLibrary.State.LIQUIDATED;
        require(!liquidated);
    }

    function repayLiquidatedLoan(uint256 loanId) public payable {
        LoanLibrary.Loan memory loan = _loanManager.getLoan(loanId);

        bool liquidated = loan.status == LoanLibrary.State.LIQUIDATED;
        require(liquidated);
    }
}
