// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

import "./Activity.sol";
import "./LoanManager.sol";
import "./OfferManager.sol";
import "./PoolManager.sol";
import "./StakingManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "../libraries/AssetLibrary.sol";
import "../data/TokenFeed.sol";

contract Lending is Context {
    PoolManager private _poolManager;
    LoanManager private _loanManager;
    OfferManager private _offerManager;
    Activity private _activity;
    StakingManager private _stakingManager;
    TokenFeed private _tokenFeed;

    constructor(
        address poolManager_,
        address loanManager_,
        address offerManager_,
        address activity_,
        address stakingManager_,
        address tokenFeed_
    ) {
        _activity = Activity(activity_);
        _poolManager = PoolManager(poolManager_);
        _loanManager = LoanManager(loanManager_);
        _offerManager = OfferManager(offerManager_);
        _stakingManager = StakingManager(stakingManager_);
        _tokenFeed = TokenFeed(tokenFeed_);
    }

    // @lenders
    function createLendingOffer(
        address principalToken,
        uint256 _principal, // only for ERC20 assets
        uint256 interest,
        uint256 daysToMaturity,
        uint256 daysToExpire
    ) public payable {
        uint256 principal;

        /* checks if the lender is a defaulter */
        require(!_activity.isDefaulter(_msgSender()), "ERR_UNSAFE_LENDER");

        /* derive the address of the principal from data repository */
        address principalToken = _tokenFeed.getTokenAddress(principalToken);

        /* extract tokens from lender */
        if (principalToken == _tokenFeed.nativeAddress()) {
            principal = msg.value;
            // (bool success, ) = _poolManager.getAddress().call{
            //     value: principal
            // }("");
            // require(success, "ERR_TRANSFER_NATIVE");
        } else {
            principal = _principal;
            IERC20(principalToken).transferFrom(
                _msgSender(),
                address(_poolManager),
                principal
            );
        }

        require(
            _poolManager.deposit(_msgSender(), principalToken, principal),
            "ERR_POOL_MANAGER_DEPOSIT"
        );

        /* allocate staking reward on principal to lender */
        require(
            _stakingManager.provideLiquidityFor(
                _msgSender(),
                principalToken,
                principal
            ),
            "ERR_STAKING_MANAGER_PROVIDE"
        );

        /* create the offer */
        require(
            _offerManager.createLendingOffer(
                principalToken,
                principal,
                interest,
                daysToMaturity,
                daysToExpire,
                _msgSender()
            ) > 0,
            "ERR_OFFER_MANAGER_CREATE"
        );
    }

    // @lenders
    function acceptBorrowingOffer(uint256 offerId) public {
        // _acceptBorrowingOffer(offerId);
    }

    // @lenders
    function acceptBorrowingRequest(uint256 requestId) public {
        _acceptBorrowingRequest(requestId);
    }

    // function _acceptBorrowingOffer(uint256 offerId) private {
    //     OfferLibrary.BorrowingOffer memory offer = _offerManager
    //         .getBorrowingOffer(offerId);
    // }

    function _acceptBorrowingRequest(uint256 requestId) private {
        RequestLibrary.BorrowingRequest memory request = _offerManager
            .getBorrowingRequest(requestId);

        require(
            !_activity.isDefaulter(request.borrower),
            "ERR_UNSAFE_BORROWER"
        );

        require(request.expiresAt < block.timestamp, "ERR_REQUEST_EXPIRED");

        require(
            _offerManager.belongsTo(
                request.offerId,
                _msgSender(),
                OfferLibrary.Type.LENDING_OFFER
            ),
            "Only lender can accept offer"
        );

        /* derive the address of the asset types from data repository */
        address principalToken = _tokenFeed.getTokenAddress(request.principalToken);
        address collateralToken = _tokenFeed.getTokenAddress(
            request.collateralToken
        );

        /* register the loan from the loan manager */
        require(
            _loanManager.createLoan(
                request.offerId,
                OfferLibrary.Type.BORROWING_OFFER,
                request.principalToken,
                request.collateralToken,
                request.principal,
                request.collateral,
                request.interest,
                request.daysToMaturity,
                request.borrower,
                _msgSender()
            ) > 0,
            "Loan manager failed"
        );

        /* transfer asset to pool manager */
        require(
            IERC20(principalToken).transferFrom(
                _msgSender(),
                address(_poolManager),
                request.principal
            ),
            "Transfer failed"
        );

        /* from the staking manager */
        /* transfer staking reward on collateral receiver from borrower to lender */
        require(
            _stakingManager.transferLiquidityTo(
                request.borrower,
                _msgSender(),
                collateralToken,
                request.collateral
            ),
            "Idle manager failed"
        );

        /* from the pool manager */
        /* transfer borrowed asset to borrower's address */

        _poolManager.transfer(
            request.borrower,
            principalToken,
            request.principal,
            principalToken == _tokenFeed.nativeAddress()
        );
    }
}
